import React, { useState } from 'react';
import { Calendar, Trash2, Edit, Plus, Image } from 'lucide-react';

function Home() {
  const [tasks, setTasks] = useState([]);
  const [moodboardItems, setMoodboardItems] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '', deadline: '' });
  const [newMoodboardUrl, setNewMoodboardUrl] = useState('');
  const [draggedItem, setDraggedItem] = useState(null);

  const handleAddTask = () => {
    if (newTask.title) {
      setTasks([...tasks, { ...newTask, id: Date.now().toString() }]);
      setNewTask({ title: '', description: '', deadline: '' });
    }
  };

  const handleDeleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const handleAddMoodboardItem = () => {
    if (newMoodboardUrl) {
      setMoodboardItems([
        ...moodboardItems,
        {
          id: Date.now().toString(),
          type: 'image',
          content: newMoodboardUrl,
          position: { x: 0, y: 0 }
        }
      ]);
      setNewMoodboardUrl('');
    }
  };

  const handleDragStart = (e, id) => {
    setDraggedItem(id);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    if (draggedItem) {
      const boardRect = e.currentTarget.getBoundingClientRect();
      const x = e.clientX - boardRect.left;
      const y = e.clientY - boardRect.top;

      setMoodboardItems(items =>
        items.map(item =>
          item.id === draggedItem
            ? { ...item, position: { x, y } }
            : item
        )
      );
      setDraggedItem(null);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-extrabold text-gray-900 mb-12 text-center">
          Gestionnaire de Projet
        </h1>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
          {/* Section Moodboard */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-semibold mb-6 flex items-center gap-3 text-gray-800">
              <Image className="w-6 h-6 text-blue-600" />
              Moodboard
            </h2>
            
            <div className="mb-6 flex gap-3">
              <input
                type="text"
                value={newMoodboardUrl}
                onChange={(e) => setNewMoodboardUrl(e.target.value)}
                placeholder="URL de l'image"
                className="flex-1 p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button
                onClick={handleAddMoodboardItem}
                className="bg-blue-500 text-white px-6 py-3 rounded-lg shadow-lg hover:bg-blue-600 flex items-center gap-2"
              >
                <Plus className="w-4 h-4" />
                Ajouter
              </button>
            </div>

            <div
              className="border-2 border-dashed border-gray-300 rounded-lg h-[400px] relative overflow-hidden"
              onDragOver={handleDragOver}
              onDrop={handleDrop}
            >
              {moodboardItems.map((item) => (
                <div
                  key={item.id}
                  draggable
                  onDragStart={(e) => handleDragStart(e, item.id)}
                  style={{
                    position: 'absolute',
                    left: `${item.position.x}px`,
                    top: `${item.position.y}px`,
                    cursor: 'move'
                  }}
                  className="w-32 h-32"
                >
                  <img
                    src={item.content}
                    alt="Moodboard"
                    className="w-full h-full object-cover rounded-lg shadow-md"
                  />
                </div>
              ))}
            </div>
          </div>

          {/* Section Tâches */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-semibold mb-6 flex items-center gap-3 text-gray-800">
              <Calendar className="w-6 h-6 text-green-600" />
              Gestion des Tâches
            </h2>

            <div className="space-y-6 mb-8">
              <input
                type="text"
                value={newTask.title}
                onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                placeholder="Titre de la tâche"
                className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500"
              />
              <textarea
                value={newTask.description}
                onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                placeholder="Description"
                className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500"
              />
              <input
                type="date"
                value={newTask.deadline}
                onChange={(e) => setNewTask({ ...newTask, deadline: e.target.value })}
                className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500"
              />
              <button
                onClick={handleAddTask}
                className="w-full bg-green-500 text-white px-6 py-3 rounded-lg shadow-lg hover:bg-green-600 flex items-center justify-center gap-2"
              >
                <Plus className="w-4 h-4" />
                Ajouter la tâche
              </button>
            </div>

            <div className="space-y-6">
              {tasks.map((task) => (
                <div key={task.id} className="border rounded-lg p-6 bg-gray-50 shadow-sm">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-semibold text-xl text-gray-800">{task.title}</h3>
                      <p className="text-sm text-gray-600 mt-2">{task.description}</p>
                      <p className="text-xs text-gray-500 mt-2">
                        Échéance: {new Date(task.deadline).toLocaleDateString()}
                      </p>
                    </div>
                    <div className="flex gap-4">
                      <button
                        onClick={() => handleDeleteTask(task.id)}
                        className="text-red-500 hover:text-red-700"
                      >
                        <Trash2 className="w-5 h-5" />
                      </button>
                      <button className="text-blue-500 hover:text-blue-700">
                        <Edit className="w-5 h-5" />
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
