import React, { useState } from 'react';
import { Calendar, Trash2, Edit, Plus, Image } from 'lucide-react';

function Home() {
  const [tasks, setTasks] = useState([]);
  const [moodboardItems, setMoodboardItems] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '', deadline: '' });
  const [newMoodboardUrl, setNewMoodboardUrl] = useState('');
  const [draggedItem, setDraggedItem] = useState(null);

  const handleAddTask = () => {
    if (newTask.title) {
      setTasks([...tasks, { ...newTask, id: Date.now().toString() }]);
      setNewTask({ title: '', description: '', deadline: '' });
    }
  };

  const handleDeleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const handleAddMoodboardItem = () => {
    if (newMoodboardUrl) {
      setMoodboardItems([
        ...moodboardItems,
        {
          id: Date.now().toString(),
          type: 'image',
          content: newMoodboardUrl,
          position: { x: 0, y: 0 }
        }
      ]);
      setNewMoodboardUrl('');
    }
  };

  const handleDragStart = (e, id) => {
    setDraggedItem(id);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    if (draggedItem) {
      const boardRect = e.currentTarget.getBoundingClientRect();
      const x = e.clientX - boardRect.left;
      const y = e.clientY - boardRect.top;

      setMoodboardItems(items =>
        items.map(item =>
          item.id === draggedItem
            ? { ...item, position: { x, y } }
            : item
        )
      );
      setDraggedItem(null);
    }
  };

  return (
    <div className="container">
      <div className="header">
        <h1>Gestionnaire de Projet</h1>
      </div>

      <div className="content">
        {/* Section Moodboard */}
        <div className="moodboard">
          <h2>
            <Image className="icon" />
            Moodboard
          </h2>

          <div className="input-container">
            <input
              type="text"
              value={newMoodboardUrl}
              onChange={(e) => setNewMoodboardUrl(e.target.value)}
              placeholder="URL de l'image"
              className="input"
            />
            <button onClick={handleAddMoodboardItem} className="button add-button">
              <Plus className="icon" />
              Ajouter
            </button>
          </div>

          <div
            className="moodboard-area"
            onDragOver={handleDragOver}
            onDrop={handleDrop}
          >
            {moodboardItems.map((item) => (
              <div
                key={item.id}
                draggable
                onDragStart={(e) => handleDragStart(e, item.id)}
                style={{
                  position: 'absolute',
                  left: `${item.position.x}px`,
                  top: `${item.position.y}px`,
                  cursor: 'move'
                }}
                className="moodboard-item"
              >
                <img
                  src={item.content}
                  alt="Moodboard"
                  className="moodboard-image"
                />
              </div>
            ))}
          </div>
        </div>

        {/* Section Tâches */}
        <div className="tasks">
          <h2>
            <Calendar className="icon" />
            Gestion des Tâches
          </h2>

          <div className="task-inputs">
            <input
              type="text"
              value={newTask.title}
              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
              placeholder="Titre de la tâche"
              className="input"
            />
            <textarea
              value={newTask.description}
              onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
              placeholder="Description"
              className="input textarea"
            />
            <input
              type="date"
              value={newTask.deadline}
              onChange={(e) => setNewTask({ ...newTask, deadline: e.target.value })}
              className="input"
            />
            <button onClick={handleAddTask} className="button task-button">
              <Plus className="icon" />
              Ajouter la tâche
            </button>
          </div>

          <div className="task-list">
            {tasks.map((task) => (
              <div key={task.id} className="task-item">
                <div className="task-header">
                  <div>
                    <h3>{task.title}</h3>
                    <p className="task-description">{task.description}</p>
                    <p className="task-deadline">
                      Échéance: {new Date(task.deadline).toLocaleDateString()}
                    </p>
                  </div>
                  <div className="task-actions">
                    <button
                      onClick={() => handleDeleteTask(task.id)}
                      className="delete-button"
                    >
                      <Trash2 className="icon" />
                    </button>
                    <button className="edit-button">
                      <Edit className="icon" />
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
